mapper.update:
		UPDATE REPORT
   		   SET PROGRESS_YN = 'Y'
 		 WHERE REPORT_ID = #{_parameter}

mapper.insert:
INSERT INTO REPORT
       (REPORT_ID
	 , REPORT_MEMBER
	 , REPORT_TYPE_ID
	 , RECEIVED_REPORT_MEMBER
	 , REPORT_CONTENT_ID
	 , REPORT_DATE
	 , REPORT_REASON
	 , REPORT_REASON_CONTENT
	 , ATTACHED_IMG
	 , PROGRESS_YN)
 VALUES ('RP-' || TO_CHAR(SYSDATE, 'YYYYMMDD') || '-' || LPAD(SEQ_REPORT_PK.NEXTVAL, 6, '0')
      , #{reportMember}
	  , #{reportTypeId}
	  , #{receivedReportMember}
	  , #{reportContentId}
	  , SYSDATE
	  , #{reportReason}
	  , #{reportReasonContent}
	  , #{attachedImg}
	  , 'N')
