mapper.update:
UPDATE GENERAL_MEMBER
   SET REGION = #{region}
 WHERE GENERAL_MEMBER_EMAIL = #{generalMemberEmail}

UPDATE GENERAL_MEMBER
   SET REGION = NULL
 WHERE GENERAL_MEMBER_EMAIL = #{generalMemberEmail}

		UPDATE GENERAL_MEMBER
		   SET GITHUB_URL = #{githubUrl}
		   	 , ADDITIONAL_EMAIL =#{additionalEmail}
             , BLOG_URL = #{blogUrl}
		 WHERE GENERAL_MEMBER_EMAIL = #{generalMemberEmail}

UPDATE GENERAL_MEMBER
   SET GITHUB_URL = NULL
 WHERE GENERAL_MEMBER_EMAIL = #{generalMemberEmail}

UPDATE GENERAL_MEMBER
   SET ADDITIONAL_EMAIL = NULL
 WHERE GENERAL_MEMBER_EMAIL = #{generalMemberEmail}

UPDATE GENERAL_MEMBER
   SET BLOG_URL = NULL
 WHERE GENERAL_MEMBER_EMAIL = #{generalMemberEmail}

