mapper.update:
UPDATE GENERAL_POST
   SET VIEW_CNT = VIEW_CNT + 1
 WHERE GENERAL_POST_ID = #{_parameter}

UPDATE GENERAL_POST
   SET POST_TITLE = #{postTitle}
     , POST_CONTENT = #{postContent}
     , POST_DATE = SYSDATE
     , POST_WRITER_ID = #{postWriterId} 
 WHERE GENERAL_POST_ID = #{generalPostId}

UPDATE GENERAL_POST
   SET POST_TITLE = #{postTitle}
     , POST_CONTENT = #{postContent}
     , POST_DATE = SYSDATE
     , POST_WRITER_ID = #{postWriterId} 
     , 'Y'		     
 WHERE GENERAL_POST_ID = #{generalPostId}
