mapper.update:
UPDATE NOTICE 
   SET POST_TITLE = #{postTitle}
	 , NOTICE_CONTENT = #{noticeContent}
	 , RELEASE_START_DATE = SYSDATE + 1
	 , RELEASE_END_DATE = SYSDATE + 8
 WHERE NOTICE_ID = #{noticeId}

UPDATE NOTICE 
   SET DELETE_YN = 'Y'
 WHERE NOTICE_ID = #{_parameter}

UPDATE NOTICE
   SET ON_OFF = #{onOff}
 WHERE NOTICE_ID = #{noticeId}
